import React from 'react';
import Card from './Card';
import { CurrentUserContext } from '../contexts/CurrentUserContext';


function Main({cards, handleEditAvatarBtn, handleEditProfileBtn, handleAddCardBtn, handleCardClick, handleCardLike, handleCardDelete}) {
  const currentUser = React.useContext(CurrentUserContext);


  return(
    <>
    <main className="content">
      <section className="profile">
        <div className="profile__avatar">
          <img src={currentUser.avatar} alt="profile-pic" className="profile__photo" />
          <button type="button" aria-label="edit-avatar" className="profile__photo_edit" onClick={handleEditAvatarBtn}></button>
        </div>
        <div className="profile__info">
          <div className="profile__title">
            <h1 className="profile__name">{currentUser.name}</h1>
            <button type="button" aria-label="edit-profile" className="profile__edit" onClick={handleEditProfileBtn}></button>
          </div>
          <p className="profile__description">{currentUser.about}</p>
        </div>
        <button aria-label="add-card-button" className="add-button" onClick={handleAddCardBtn}></button>
      </section>

      <section className="elements">
        <ul className="elements__list">
          {
            cards.map((card, id) =>
              <Card
                key={id}
                card={card}
                alt={card.alt}
                src={card.src}
                title={card.title}
                likes={card.likes}
                _id={card._id}
                owner={card.owner}
                onCardClick={() => handleCardClick(card)}
                onDeleteClick={(card) => handleCardDelete(card)}
                onLickeClick={(card) => handleCardLike(card)}
              />
            )
          }
        </ul>
      </section>
    </main>
    </>
  )
}


export default Main;
