import React from 'react';
import { CurrentUserContext } from '../contexts/currentUserContext';
import { defaultSettings, profileConfig, cardsConfig, popupConfig, avatarImage, avatarPicInput, avatarEditButton,
  descriptionInput, nameInput, submitAvatar, submitCard, submitEdit,projectId } from '../utils/constants';
import Api from './Api';
import Card from './Card';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import AddCardPopup from './AddCardPopup';
import EditAvatarPopup from './EditAvatarPopup';
import EditProfilePopup from './EditProfilePopup';
import PopupWithForm from './PopupWithForm';
import PopupWithImage from './PopupWithImage';

const [currentUser, setCurrentUser] = React.useState({});
const [cards, setCards] = React.useState({});
//popupus
const [isEditAvatar, setEditAvatar] = React.useState(false);
const [isEditProfile, setEditProfile] = React.useState(false);
const [isAddNewCard, setAddNewCard] = React.useState(false);
const [isDeleteCard, setDeleteCard] = React.useState(false);
const [isImageExpand, setImageExpand] = React.useState(false);
//expand image elements
const [imageLink, setImageLink] = React.useState('');
const [imageTitle, setImageTitle] = React.useState('');

const api = new Api({
  baseUrl: "https://around.nomoreparties.co/v1/group-5",
  headers: {
    authorization: projectId,
    "Content-Type": "application/json"
  }
});

export default class App extends React.Component {
  constructor(props) {
    super(props);
  }
  handleEditAvatarBtn() {
    setEditAvatar(true);
  }
  handleEditProfileBtn() {
    setEditProfile(true);
  }
  handleEditAddCardBtn() {
    setAddNewCard(true);
  }
  handlePopupClose() {
    setEditAvatar(false);
    setEditProfile(false);
    setAddNewCard(false);
    setImageExpand(false);
    setDeleteCard(false);
  }
  handleImageExpand() {
    setImageTitle(name);
    setImageLink(link);
    setImageExpand(true);
  }

  handleDeleteCard(cardId) {

  }

  handleCardLike(cardId) {

  }
  //collect info from server: user and cards
  render() {
    api.getAppInfo() //[api.getUserInfo(), api.getInitialCards()]
      .then(([userData, initialCards]) => {
        //retrieve user profile info from server
        api.getUserInfo()
          .then(() => {
            setCurrentUser({
              name: userData.name,
              about: userData.about,
              avatar: userData.avatar
            });
          })
          .catch(err => console.log(err));

        //collect cards from server
        api.getInitialCards()
          .then(() => {
            setCards(initialCards.map((card) => ({
              title: card.name,
              link: card.link,
              _id: card._id,
              likes: card.likes,
              userId: card.owner
            })));
          })
          .catch(err => console.log(err));
      })
  }


  // return (
  //   <div className="body">
  //   </div>
  // );

}



