import React from 'react';
import { projectId } from '../utils/constants';
import Api from './Api';
import Card from './Card';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import AddCardPopup from './AddCardPopup';
import EditAvatarPopup from './EditAvatarPopup';
import EditProfilePopup from './EditProfilePopup';
import PopupWithForm from './PopupWithForm';
import PopupWithImage from './PopupWithImage';
import Input from './Input';
import Button from './Button';

function App() {

  //popupus
  const [isAddNewCard, setAddNewCardPopup] = React.useState(false);
  const [isEditAvatar, setEditAvatarPopup] = React.useState(false);
  const [isEditProfile, setEditProfilePopup] = React.useState(false);
  const [isDeleteCard, setDeleteCardPopup] = React.useState(false);
  const [isImageExpand, setImageExpand] = React.useState(false);
  //expand image elements
  const [imageLink, setImageLink] = React.useState('');
  const [imageTitle, setImageTitle] = React.useState('');

  const api = new Api({
    baseUrl: "https://around.nomoreparties.co/v1/group-5",
    headers: {
      authorization: projectId,
      "Content-Type": "application/json"
    }
  });

  //handle click on buttons
  function handleEditAvatarBtn() {
    setEditAvatarPopup(true);
  }
  function handleEditProfileBtn() {
    setEditProfilePopup(true);
  }
  function handleEditAddCardBtn() {
    setAddNewCardPopup(true);
  }
  function handlePopupClose(evt) {
    if(evt.target !== evt.currentTarget) return;

    setEditAvatarPopup(false);
    setEditProfilePopup(false);
    setAddNewCardPopup(false);
    setDeleteCardPopup(false);
    setImageExpand(false);
  }
  function handleDeleteCard(card) {
    setDeleteCardPopup(true);
    api.removeCard(card._id)
      .then(() => {
        card.remove();
        setDeleteCardPopup(false);
      })
      .catch(err => console.log(err));
  }
  function handleLikeClick(card) {
    // const isLiked = card.likes.some((like) => like._id === currentUser._id);

    // if(isLiked) {
    //   api.removeCardLike(cardId)
    //     .then(res => {
    //       card._element.querySelector('.element__like').classList.remove('element__like_active');
    //         card.likesCount(res.likes.length)
    //     })
    //     .catch(err => console.log(err))

    // } else {
    //   api.addCardLike(cardId)
    //     .then(res => {
    //       card._element.querySelector('.element__like').classList.add('element__like_active');
    //       return card.likesCount(res.likes.length)
    //     })
    //     .catch(err => console.log(err))
    // }
  }

  function handleAvatarUpdate(avatar) {
    api.setUserAvatar({avatar})
      .then((res) => {
        setCurrentUser({...setCurrentUser})
      })
      .then(() => handlePopupClose()
      .catch(err => console.log(err)));
  }


  function handleImageExpand(name, link) {
    setImageTitle(name);
    setImageLink(link);
    setImageExpand(true);
  }





  return (
    <>
    <Header />
    <Main />
    <Footer />
      <EditAvatarPopup isOpen={isEditAvatar} onClose={handlePopupClose} handleAvatarUpdate={handleAvatarUpdate} />
      <EditProfilePopup />
      <AddCardPopup />
      <PopupWithForm />
      <PopupWithImage link={setImageLink} name={setImageTitle} isOpen={isImageExpand} onClose={handlePopupClose} />
    </>
  );

}

export default App;
