import React from 'react';
import PopupWithForm from './PopupWithForm';

function AddCardPopup(props) {
  const [imageTitle, setImageTitle] = React.useState('');
  const [imageLink, setImageLink] = React.useState('');

  function handleTitleChange(evt) {
    setImageTitle(evt.target.value);
  }
  function handleImageLinkChange(evt) {
    setImageLink(evt.target.value);
  }
  function handleFormSubmit(evt) {
    evt.preventDefault();
    props.handleAddCard({
      title: imageTitle,
      src: imageLink
    });
  }

  return (
    <PopupWithForm name="add" title="New Place" submitButton="Save" isOpen={props.isOpen} onClose={props.onClose} onSubmit={handleFormSubmit}>
      <input id="card-title" type="text" name="name" value={imageTitle} onChange={handleTitleChange} className="form__input form__input_type_card-title" placeholder="Title" minLength="2" maxLength="30" required />
      <span id="card-title-error" className="form__field form__field_error"></span>
      <input id="card-url" type="url" name="link" value={imageLink} onChange={handleImageLinkChange} className="form__input form__input_type_card-url" placeholder="Image link" required />
      <span id="card-url-error" className="form__field form__field_error"></span>
    </PopupWithForm>

    // <div className="popup popup_type_add-card">
    //   <form id="addForm" name="add" className="form form_add-card popup__form">
