import React from 'react';
import PopupWithForm from './PopupWithForm';

function EditAvatarPopup(props) {
  const avatarLink = React.useRef();

  function handleSubmitHandler(evt) {
    evt.preventDefault();
    props.handleUpdateAvatar(avatarLink.value);
  }

  return (

    <div className="popup popup_type_edit-avatar">
      <form id="avatar" name="" className="form form_edit-avatar popup__form">
        <h2 className="form__title"></h2>
        <fieldset className="form__field">
        </fieldset>
        <button type="submit" aria-label="save-edit-avatar" className="form__submit">Save</button>
        <button type="button" aria-label="close-edit-avatar" className="popup__close form__close-button"></button>
      </form>
    </div>
  )
}

export default EditAvatarPopup;
