import React from 'react';
import { CurrentUserContext } from '../contexts/currentUserContext';

function Card(props) {
  const currentUser = React.useContext(CurrentUserContext);

  //check if current user is owner of the card
  const isOwner = props.owner._id === currentUser._id;
  //buttons
  const deletBtn = (`element__delete ${isOwner ? 'element__delete_show' : ''}`);
  const isLiked = props.likes.some((like) => like._id === this._userId);
  const likeBtn = (`element__like ${isLiked ? 'element__like_active' : ''}`);

  function handleDeleteClick() {
    props.handleDeleteCard(props.cardId);
  }

  function handleLikeBtnClick() {
    props.handleCardLike(props.cardId);
  }

  return (
    <template className="element">
      <li className="element__item">
        <button type="button" aria-label="delete-card" className="element__delete"></button>
        <img className="element__photo" src="#" alt="#" />
        <div className="element__details">
          <h2 className="element__name"></h2>
          <div className="element__likes">
            <button type="button" aria-label="like-card" className="element__like"></button>
            <p className="element__like_count"></p>
          </div>
        </div>
      </li>
    </template>
  )
}
