import React from 'react';
import { CurrentUserContext } from '../contexts/currentUserContext';
import { projectId } from '../utils/constants';
import Api from './Api';
import Card from './Card';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import AddCardPopup from './AddCardPopup';
import EditAvatarPopup from './EditAvatarPopup';
import EditProfilePopup from './EditProfilePopup';
import PopupWithForm from './PopupWithForm';
import PopupWithImage from './PopupWithImage';
import Input from './Input';
import Button from './Button';

function App() {
  const [currentUser, setCurrentUser] = React.useState({});
  const [cards, setCards] = React.useState({});
  //popupus
  const [isEditAvatar, setEditAvatar] = React.useState(false);
  const [isEditProfile, setEditProfile] = React.useState(false);
  const [isAddNewCard, setAddNewCard] = React.useState(false);
  const [isDeleteCard, setDeleteCard] = React.useState(false);
  const [isImageExpand, setImageExpand] = React.useState(false);
  //expand image elements
  const [imageLink, setImageLink] = React.useState('');
  const [imageTitle, setImageTitle] = React.useState('');

  const api = new Api({
    baseUrl: "https://around.nomoreparties.co/v1/group-5",
    headers: {
      authorization: projectId,
      "Content-Type": "application/json"
    }
  });
  function handleEditAvatarBtn() {
    setEditAvatar(true);
  }
  function handleEditProfileBtn() {
    setEditProfile(true);
  }
  function handleEditAddCardBtn() {
    setAddNewCard(true);
  }
  function handlePopupClose() {
    setEditAvatar(false);
    setEditProfile(false);
    setAddNewCard(false);
    setImageExpand(false);
  }
  function handleImageExpand(name, link) {
    setImageTitle(name);
    setImageLink(link);
    setImageExpand(true);
  }

  function handleDeleteCard(card) {
    setDeleteCard(true);
    api.removeCard(card._id)
      .then(() => {
        card.remove();
        setDeleteCard(false);
      })
      .catch(err => console.log(err));
  }

  function handleLikeClick(card) {
    // const isLiked = card.likes.some((like) => like._id === currentUser._id);

    // if(isLiked) {
    //   api.removeCardLike(cardId)
    //     .then(res => {
    //       card._element.querySelector('.element__like').classList.remove('element__like_active');
    //         card.likesCount(res.likes.length)
    //     })
    //     .catch(err => console.log(err))

    // } else {
    //   api.addCardLike(cardId)
    //     .then(res => {
    //       card._element.querySelector('.element__like').classList.add('element__like_active');
    //       return card.likesCount(res.likes.length)
    //     })
    //     .catch(err => console.log(err))
    // }
  }

  //collect info from server: user and cards
  React.useEffect(() => {
    api.getAppInfo() //[api.getUserInfo(), api.getInitialCards()]
      .then(([userData, initialCards]) => {
        //retrieve user profile info from server
        api.getUserInfo()
          .then(() => {
            setCurrentUser({
              name: userData.name,
              about: userData.about,
              avatar: userData.avatar
            });
          })
          .catch(err => console.log(err));

        //collect cards from server
        api.getInitialCards()
          .then(() => {
            setCards(initialCards.map((card) => ({
              title: card.name,
              link: card.link,
              _id: card._id,
              likes: card.likes,
              userId: card.owner
            })));
          })
          .catch(err => console.log(err));
      })
    });

  return (
    <div className="body">
    </div>
  );

}

