import React from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext';


function Card(props) {
  // current user context
  const currentUser = React.useState(CurrentUserContext);

  const cardDeleteButtonClassName = (`element__delete ${isOwn ? 'element__delete_show' : 'element__delete_hidden'}`);
  // Check if the card was liked by the current user
  const isLiked = props.card.likes.some(i => i._id === currentUser._id);

  // Create a variable which you then set in `className` for the like button
  const cardLikeButtonClassName = (`element__like ${isLiked ? 'element__like_active' : ''}`);

  return (
    <li className="element__item">
      <button type="button" aria-label="delete-card" className={cardDeleteButtonClassName} onClick={props.isDeleteCard}></button>
      <img className="element__photo" src={props.src} alt={props.alt} onClick={props.handleCardClick} />
      <div className="element__details">
        <h2 className="element__name">{props.title}</h2>
        <div className="element__likes">
          <button type="button" aria-label="like-card" className={cardLikeButtonClassName} onClick={props.handleLikeBtnClick}></button>
          <p className="element__like_count">{props.likes.length}</p>
        </div>
      </div>
    </li>
  )
}

export default Card;
