import React from 'react';
// import { popupEditAvatar, popupEditProfile, popupAddCard } from './App';
import Card from './Card';
import { CurrentUserContext } from '../contexts/currentUserContext';
 const popupEditProfile = document.querySelector('.popup_type_edit-profile');
 const popupAddCard = document.querySelector('.popup_type_add-card');
 const popupEditAvatar = document.querySelector('.popup_type_edit-avatar');

console.log(popupAddCard);
console.log(popupEditAvatar);
console.log(popupEditProfile);
function Main(props) {
  const currentUser = React.useContext(CurrentUserContext);

  function handleEditAvatarClick() {
    console.log(2);
    console.log(popupEditAvatar);
    popupEditAvatar.classList.add('popup_open');
  }

  function handleEditProfileClick() {
    console.log(1);
    console.log(popupEditProfile);
    popupEditProfile.classList.add('popup_open');
  }

  function handleAddPlaceClick() {
    console.log(3);
    console.log(popupAddCard);
    popupAddCard.classList.add('popup_open');
  }

  const [cardTitle, setCardTitle] = React.useState('');
  const [cards, setCards] = React.useState([]);
  //collect info from server: user and cards
  React.useEffect(() => {
    console.log('123')
    api.getInitialCards()
      .then((res) => {
        setCards(res.map(cardItem => ({
          alt: cardItem.name,
          title: cardItem.name,
          src: cardItem.link,
          id: cardItem.id
        })))
      })
    }, [cardTitle])


  return(
    <main className="content">
      <section className="profile">
        <div className="profile__avatar">
          <img src={currentUser.avatar} alt="profile-pic" className="profile__photo" />
          <button type="button" aria-label="edit-avatar" className="profile__photo_edit" onClick={handleEditAvatarClick}></button>
        </div>
        <div className="profile__info">
          <div className="profile__title">
            <h1 className="profile__name">{currentUser.name}</h1>
            <button type="button" aria-label="edit-profile" className="profile__edit" onClick={handleEditProfileClick}></button>
          </div>
          <p className="profile__description">{currentUser.about}</p>
        </div>
        <button aria-label="add-card-button" className="add-button" onClick={handleAddPlaceClick}></button>
      </section>

      <section className="elements">
        <ul className="elements__list">
          {
            cards.map((card, id) => <Card key={id} {...card} />)
          }
        </ul>
      </section>
    </main>
  )
}


export default Main;
