import React from 'react';
import Input from './Input';

function PopupWithImage(props) {
  return (
    <div className={`popup popup_type_image ${props.isOpen ? "popup_open" : ""}`} onClick={props.onClose}>
      <div className="popup__content">
        <button type="button" aria-label="close-expand-card" className="popup__close" onClick={props.onClose}></button>
        <figure className="popup__figure">
          <img  className="popup__image" src={props.link} alt={props.name} />
          <figcaption className="popup__image-caption">{props.name}</figcaption>
        </figure>
      </div>
    </div>
  )
}

export default PopupWithImage;
