function Main() {
  const popupEditProfile = document.querySelector('.popup_type_edit-profile');
  const popupAddCard = document.querySelector('.popup_type_add-card');
  const popupEditAvatar = document.querySelector('.popup_type_edit-avatar');

  function handleEditAvatarClick() {
    console.log(2);
    console.log(popupEditAvatar);
    popupEditAvatar.classList.add('popup_open');
  }

  function handleEditProfileClick() {
    console.log(1);
    console.log(popupEditProfile);
    popupEditProfile._element.classList.add('popup_open');
  }

  function handleAddPlaceClick() {
    console.log(3);
    console.log(popupAddCard);
    popupAddCard.classList.add('popup_open');
  }

  return(
    <main className="content">
      <section className="profile">
        <div className="profile__avatar">
          <img src="#" alt="profile-pic" className="profile__photo" />
          <button type="button" aria-label="edit-avatar" className="profile__photo_edit" onClick={handleEditAvatarClick}></button>
        </div>
        <div className="profile__info">
          <div className="profile__title">
            <h1 className="profile__name"></h1>
            <button type="button" aria-label="edit-profile" className="profile__edit" onClick={handleEditProfileClick}></button>
          </div>
          <p className="profile__description"></p>
        </div>
        <button aria-label="add-card-button" className="add-button" onClick={handleAddPlaceClick}></button>
      </section>

      <section className="elements">
        <ul className="elements__list"></ul>
      </section>
    </main>
  )
}


export default Main;
