import React from 'react';
// import { popupEditAvatar, popupEditProfile, popupAddCard } from './App';
import Card from './Card';
import Api from './Api';
import { projectId } from '../utils/constants';
import { CurrentUserContext } from '../contexts/currentUserContext';
 const popupEditProfile = document.querySelector('.popup_type_edit-profile');
 const popupAddCard = document.querySelector('.popup_type_add-card');
 const popupEditAvatar = document.querySelector('.popup_type_edit-avatar');

function Main(props) {
  const currentUser = React.useContext(CurrentUserContext);

  function handleEditAvatarClick() {
    console.log(2);
    console.log(popupEditAvatar);
    popupEditAvatar.classList.add('popup_open');
  }

  function handleEditProfileClick() {
    console.log(1);
    console.log(popupEditProfile);
    popupEditProfile.classList.add('popup_open');
  }

  function handleAddPlaceClick() {
    console.log(3);
    console.log(popupAddCard);
    popupAddCard.classList.add('popup_open');
  }

  const api = new Api({
    baseUrl: "https://around.nomoreparties.co/v1/group-5",
    headers: {
      authorization: projectId,
      "Content-Type": "application/json"
    }
  });
  const [cardTitle, setCardTitle] = React.useState('');
  const [cards, setCards] = React.useState([]);
  const [userName, setUserName] = React.useState('');
  const [userDescription, setUserDescription] = React.useState('');
  const [userAvatar, setUserAvatar] = React.useState('');

  //collect info from server: user info
  React.useEffect(() => {
    console.log('user info collected!')
    api.getUserInfo()
      .then((res) => {
        setUserAvatar(res.avatar);
        setUserName(res.name);
        setUserDescription(res.about);
      })
      .catch(err => console.log(err))
  }, [])

  //collect info from server: cards
  React.useEffect(() => {
    api.getInitialCards()
      .then((res) => {
        setCards(res.map(cardItem => ({
          alt: cardItem.name,
          title: cardItem.name,
          src: cardItem.link,
          id: cardItem._id,
          owner: cardItem.owner,
          likes: cardItem.likes
        })))
      })
      .catch(err => console.log(err))
    }, [cardTitle])


  return(
    <main className="content">
      <section className="profile">
        <div className="profile__avatar">
          <img src={userAvatar} alt="profile-pic" className="profile__photo" />
          <button type="button" aria-label="edit-avatar" className="profile__photo_edit" onClick={handleEditAvatarClick}></button>
        </div>
        <div className="profile__info">
          <div className="profile__title">
            <h1 className="profile__name">{userName}</h1>
            <button type="button" aria-label="edit-profile" className="profile__edit" onClick={handleEditProfileClick}></button>
          </div>
          <p className="profile__description">{userDescription}</p>
        </div>
        <button aria-label="add-card-button" className="add-button" onClick={handleAddPlaceClick}></button>
      </section>

      <section className="elements">
        <ul className="elements__list">
          {
            cards.map((card, id) => <Card key={id} {...card} />)
          }
        </ul>
      </section>
    </main>
  )
}


export default Main;
