import React from 'react';
import api from '../utils/Api';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import PopupWithImage from './PopupWithImage';

function App() {
  const [currentUser, setCurrentUser] = React.useState('');

  //popupus
  const [isAddNewCard, setAddNewCardPopup] = React.useState(false);
  const [isEditAvatar, setEditAvatarPopup] = React.useState(false);
  const [isEditProfile, setEditProfilePopup] = React.useState(false);
  const [isDeleteCard, setDeleteCardPopup] = React.useState(false);
  const [isImageExpand, setImageExpand] = React.useState(false);
  //expand image elements
  const [imageLink, setImageLink] = React.useState('');
  const [imageTitle, setImageTitle] = React.useState('');
  const [selectedCard, setSelectedCard] = React.useState('');
  //save user inputs
  const [avatartLink, setAvatarLink] = React.useState('');
  const [nameInput, setNameInput] = React.useState('');
  const [descriptionInput, setDescriptionInput] = React.useState('');

  //handle click on buttons
  function handleEditAvatarBtn() {
    setEditAvatarPopup(true);
  }
  function handleEditProfileBtn() {
    setEditProfilePopup(true);
  }
  function handleEditAddCardBtn() {
    setAddNewCardPopup(true);
  }
  function handlePopupClose(evt) {
    if(evt.target !== evt.currentTarget) return;

    setEditAvatarPopup(false);
    setEditProfilePopup(false);
    setAddNewCardPopup(false);
    setDeleteCardPopup(false);
    setSelectedCard(false);
  }
  function handleDeleteCardBtn() {
    setDeleteCardPopup(true)
  }
  function handleDeleteCard(card) {
    api.removeCard(card._id)
      .then(() => {
        card.remove();
        setDeleteCardPopup(false);
      })
      .catch(err => console.log(err));
  }
  function handleLikeClick(card) {

  }

  function handleAvatarUpdate(avatar) {
    api.setUserAvatar({avatar})
      .then((res) => {
        setCurrentUser({...setCurrentUser})
      })
      .then(() => handlePopupClose()
      .catch(err => console.log(err)));
  }

  function handleCardClick(name, link) {
    setImageTitle(name);
    setImageLink(link);
    selectedCard(true);
  }

  function handleTitleChange(evt) {
    setImageTitle(evt.target.value);
  }
  function handleImageLinkChange(evt) {
    setImageLink(evt.target.value);
  }
  function handleNameChange(evt) {
    setNameInput(evt.target.value);
  }
  function handleDescriptionChange(evt) {
    setDescriptionInput(evt.target.value);
  }
  function handleFormSubmit(evt) {
    evt.preventDefault();
    setImageLink(imageTitle);
    setImageLink(imageLink);
  }
  function handleEscClose(evt) {
    if(evt.which === 27) {
      this.close();
    }
  }


  return (
    <>
      <Header />
      <Main
        handleEditAvatarBtn={handleEditAvatarBtn}
        handleEditProfileBtn={handleEditProfileBtn}
        handleEditAddCardBtn={handleEditAddCardBtn}
        handleDeleteCardBtn={handleDeleteCardBtn}
        handleCardClick={(name, link) => {handleCardClick(name, link)}}
      />
      <Footer />

      {/* Edit avatar popup */}
      <PopupWithForm name="avatar" title="Change Profile Picture" submitButton="Save" isOpen={isEditAvatar} onClose={handlePopupClose} >
        <input id="avatar-url" type="url" name="link" onChange={handleAvatarUpdate} className="form__input form__input_type_avatar-url" placeholder="Avatar link" required />
        <span id="avatar-url-error" className="form__field form__field_error"></span>
      </PopupWithForm>

      {/* Edit Profile popup */}
      <PopupWithForm name="profile" title="Edit Profile" submitButton="Save" isOpen={isEditProfile} onClose={handlePopupClose} >
        <input id="card-title" type="text" name="name" value={nameInput} onChange={handleNameChange} className="form__input form__input_type_card-title" placeholder="Name" minLength="2" maxLength="30" required />
        <span id="card-title-error" className="form__field form__field_error"></span>
        <input id="card-url" type="url" name="link" value={descriptionInput} onChange={handleDescriptionChange} className="form__input form__input_type_card-url" placeholder="About Me" required />
        <span id="card-url-error" className="form__field form__field_error"></span>
      </PopupWithForm>

      {/* Add New Card Popup */}
      <PopupWithForm name="add" title="New Place" submitButton="Save" isOpen={isAddNewCard} onClose={handlePopupClose} onSubmit={handleFormSubmit}>
      <input id="card-title" type="text" name="name" value="" onChange={handleTitleChange} className="form__input form__input_type_card-title" placeholder="Title" minLength="2" maxLength="30" required />
      <span id="card-title-error" className="form__field form__field_error"></span>
      <input id="card-url" type="url" name="link" value="" onChange={handleImageLinkChange} className="form__input form__input_type_card-url" placeholder="Image link" required />
      <span id="card-url-error" className="form__field form__field_error"></span>
    </PopupWithForm>

    {/* Delete card confirmation */}
    <PopupWithForm name="delete" title="Are You Sure?" submitButton="Yes" isOpen={isDeleteCard} onClose={handlePopupClose} onSubmit={handleDeleteCard} />

    {/* Expand card on full-screen */}
    <PopupWithImage link={imageLink} name={imageTitle} isOpen={isImageExpand} onClose={handlePopupClose} />


    </>
  );

}

export default App;
