import Input from './Input';
import Button from './Button';

function PopupWithForm(props) {
  return (
    <>
    <div className={`popup popup_type_${props.name} ${props.isOpen ? "popup_open" : ""}`}>
      <form id="avatar" name="avatar" className="form popup__form `form_${props.name}`">
        <h2 className="form__title">{props.title}</h2>
        <fieldset className="form__field">
          <Input placeholder='Avatar link' changeHandler={() => {console.log('changed!')}} />
          {/* <input id="avatar-url" type="url" name="link" className="form__input form__input_type_avatar-url" placeholder="Avatar link" required /> */}
          <span id="avatar-url-error" className="form__field form__field_error"></span>
        </fieldset>
        <Button innerText={'save'} onClick={() => {console.log('clicked!')}}/>
        <button type="submit" aria-label="save-edit-avatar" className="form__submit">{props.buttonText}</button>
        <button type="button" aria-label="close-edit-avatar" className="popup__close form__close-button" onClick={props.onClose}></button>
      </form>
    </div>
    </>
  )
}

export default PopupWithForm;
